export const getQuestionsForJob = (job, limit = DEFAULT_QUESTION_LIMIT) => {
  if (!job) return [];
  const list = interviewQuestionsByJob[job];
  if (!Array.isArray(list) || list.length === 0) return [];
  return typeof limit === "number" ? list.slice(0, Math.max(0, limit)) : list;
};
